import Ember from 'ember';

export default Ember.Route.extend({
  ajax: Ember.inject.service(),

  model: function(params) {
    return this.get('ajax').request('/assets/js/data.js')
      .then(data =&gt; data.findBy('category', params.category));
  }
});
